

import pytest

from audiometa import get_specific_metadata
from audiometa.utils.UnifiedMetadataKey import UnifiedMetadataKey


@pytest.mark.integration
class TestTitleReading:
    def test_id3v1(self, metadata_id3v1_small_mp3):
        title = get_specific_metadata(metadata_id3v1_small_mp3, UnifiedMetadataKey.TITLE)
        assert title == "aaaaaaaaaaaaaaaaaaaaaaaaaaaaaa"

    def test_id3v2(self, metadata_id3v2_small_mp3):
        title = get_specific_metadata(metadata_id3v2_small_mp3, UnifiedMetadataKey.TITLE)
        assert title == ""

    def test_vorbis(self, metadata_vorbis_small_flac):
        title = get_specific_metadata(metadata_vorbis_small_flac, UnifiedMetadataKey.TITLE)
        assert title == ""

    def test_riff(self, metadata_riff_small_wav):
        title = get_specific_metadata(metadata_riff_small_wav, UnifiedMetadataKey.TITLE)
        assert title == ""
